import React, { Component } from 'react';
import './App.css';
import "bootstrap/dist/css/bootstrap.css";

class App extends Component {
  state = { 
    total: ""
  }

  render() { 
    return (
      <div id="total">
        <div>
          <h1>Total: {this.state.total}</h1>
        </div>
        <div>
          <button id="first" type="button" class="btn m-3 btn-secondary"><h2>1</h2></button>
          <button id= "second" type="button" class="btn m-3 btn-secondary">2</button>
          <button id="third" type="button" class="btn m-3 btn-secondary">3</button>
        </div>
        <div>
          <button id="forth" type="button" class="btn m-3 btn-secondary">4</button>
          <button id="fifth" type="button" class="btn m-3 btn-secondary">5</button>
          <button id="sixth" type="button" class="btn m-3 btn-secondary">6</button>
        </div>
        <div>
          <button id='seventh' type="button" class="btn m-3 btn-secondary">7</button>
          <button id="eighth" type="button" class="btn m-3 btn-secondary">8</button>
          <button id="ninth" type="button" class="btn m-3 btn-secondary">9</button>
        </div>
        <div>
          <button id="tenth" type="button" class="btn m-3 btn-secondary">0</button>
          <button id="eleventh" type="button" class="btn m-3 btn-secondary">Restart</button>
        </div>
      </div>
     );
  }
}

export default App;
